import { Component } from '@angular/core';
import { TasksService } from '../tasks.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-list-tasks',
  templateUrl: './list-tasks.component.html',
  styleUrls: ['./list-tasks.component.css']
})
export class ListTasksComponent {

  tasks: any;

  constructor(
    private tasksService: TasksService,
    private router: Router,
  ) {

  }

  ngOnInit(): void {
    this.tasksService.listTasks()
    .subscribe((data: any) => {
      this.tasks = data.Tasks;
    })
  }


  deleteTask(taskId: any): void {
    this.tasksService.deleteTasks(taskId)
      .subscribe((data: any) => {
        setTimeout(() => {
          this.tasksService.listTasks()
            .subscribe((data: any) => {
              this.tasks = data.Tasks;
            })
        }, 100);
      });
  }

}
<div class="container">

    <h2>List of tasks...</h2>
    <p></p>

    <table class="table table-striped">

        <thead>
            <th>Done</th>
            <th>Task</th>
            <th>Action</th>
        </thead>

        <p></p>

        <tbody>
            <tr *ngFor="let t of tasks">
                <td>
                    <input name="cb" type="checkbox" id="cb">    
                </td>
                <td>{{ t.content }}</td>
                <td>
                    <button class="btn btn-warning"><a [routerLink]="['/update-task/',t.id]">Edit</a></button>
                    <button (click)="deleteTask(t.id)" class="btn btn-danger">Delete</button>
                </td>
            </tr>
        </tbody>
    </table>
</div>
