import { Component } from '@angular/core';
import { TasksService } from '../tasks.service';
import { Router } from '@angular/router';
import { Tasks } from '../tasks.model';

@Component({
  selector: 'app-list-tasks',
  templateUrl: './list-tasks.component.html',
  styleUrls: ['./list-tasks.component.css']
})
export class ListTasksComponent {

  tasks: any;

  constructor(
    private tasksService: TasksService,
    private router: Router,
  ) {

  }

  ngOnInit(): void {
    this.tasksService.listTasks()
    .subscribe((data: any) => {
      this.tasks = data.Tasks;
    })
  }


  deleteTask(taskId: any): void {
    this.tasksService.deleteTasks(taskId)
      .subscribe((data: any) => {
        setTimeout(() => {
          this.tasksService.listTasks()
            .subscribe((data: any) => {
              this.tasks = data.Tasks;
            })
        }, 100);
      });
  }

  markAsComplete(task: any) {
    task.completed = true;

    this.tasksService.markTaskAsComplete(task.id)
      .subscribe(
        response => {
          console.log('Task marked as complete successfully:', response);

        },
        error => {
          console.error('Error marking task as complete:', error);

        }
      );
  }

}
<div class="container">
    <div class="row align-items-end">
        <p></p>
        <div class="col">
            <h2 class="">List of tasks...</h2>
        </div>

        <div class="col align-self-end">
            <div class="container text-end">
                <button class="btn btn-primary"><a [routerLink]="['/add-task/']">Add Task</a></button>
            </div> 
        </div>
    </div>
</div>
<p></p>
<div class="container">
    <div class="row p-3 border bg-light" id="thead">
        <div class="col-md-1 text-center" id="duedate">Due Date</div>
        <div class="col-md-6 text-center">Tasks</div>
        <div class="col-md-4 text-center">Actions</div>
    </div>
</div>
<div class="container">
    <div *ngFor="let t of tasks" id="cnt">
        <div class="row gy-5">    
            <div class="col-md-2 p-3 border bg-light text-center">{{ t.dueDate | date:'dd-MM-yyyy' }}</div>
            <div class="col-md-6 p-3 border bg-light" [class.completed]="t.completed">{{ t.content }}</div>
            <div class="col-md-4 p-3 border bg-light text-center">
                <button *ngIf="!t.completed" (click)="markAsComplete(t)" class="btn btn-success btn-margin">Mark as Complete</button>
                <button *ngIf="!t.completed" class="btn btn-warning btn-margin"><a [routerLink]="['/update-task/', t.id]">Edit</a></button>
                <button (click)="deleteTask(t.id)" class="btn btn-danger btn-margin">Delete</button>
            </div>
        </div>
    </div>
</div>
