import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { TasksService } from '../tasks.service';

@Component({
  selector: 'app-update-task',
  templateUrl: './update-task.component.html',
  styleUrls: ['./update-task.component.css']
})
export class UpdateTaskComponent {
  angForm : FormGroup;
  error: any;
  subscription: any;
  id: any;
  task: any;
  tasks: any;
  success: string | undefined;

  constructor(
    private fb: FormBuilder,
    private route: Router,
    private tasksService: TasksService,
    private activeRoute: ActivatedRoute
  ) {

    this.angForm = this.fb.group({
      id: [''],
      dueDate: [''],
      content: ['', Validators.required],
    });

    this.subscription = this.activeRoute.paramMap.subscribe(params => {
      this.id = params.get('id');
    })
  }

  ngOnInit(): void {
    this.getSingleTask(this.id);
  }

  getSingleTask(id: any): void {
    this.tasksService.getTaskById(id)
      .subscribe((res: any) => {
        this.task = res;
        this.angForm.patchValue(this.task);
        console.log(res);
      },
      (err: any) => {
        this.error = err;
      });
  }  

  editData(f: any) {
    console.log(f.value);
    this.tasksService.updateTask(this.id, f.value)
      .subscribe((res: any) => {
        this.tasks = res;
        this.success = "Updated successfully";
        this.route.navigate(['/list-tasks']);
      },
      (err: any) => {
        this.error = err;
      })
  }
}
<div class="container extra_container">
    <div class="row">
        <p></p>
        <div class="col">
            <h2>Update Task...</h2>
        </div>
        <div class="col"></div>
        <div class="col">
            <div class="container text-end">
                <button class="btn btn-primary"><a [routerLink]="['/list-tasks/']">Back</a></button>
            </div> 
        </div>
</div>

<div class="container border bg-light">
    <form [formGroup]="angForm" (ngSubmit)="editData(angForm)" autocomplete="off">
        <input type="hidden" name="id" formControlName="id">

        <div class="row align-items-center mt-3 ">
            <div class="col-md-4 text-center" id="cnt">Content</div>
            <div class="col-md-6"><input type="text" class="form-control" name="content" formControlName="content"></div>
        </div>

        <div class="row align-items-center mt-3">
            <div class="col-md-4 text-center" id="dud">Due Date</div>
            <div class="col-md-6"><input type="date" class="form-control" name="dueDate" formControlName="dueDate"></div>
        </div>

        <div class="row align-items-center mt-3">
            <div class="col-md-12 text-center"><input type="submit" class="btn btn-warning" name="submit" value="Update"></div>
        </div>
    </form>  
</div>
